BEGIN TRAN
	DECLARE 
        @ID_DEFAULT INT,
		@ID_COMPANY INT,
		@ID_USER INT,
        @ID_PROFILE INT;
	
	BEGIN
		SET @ID_DEFAULT = 100;

        /* CRIA EMPRESA */
		SET IDENTITY_INSERT FRW_COMPANY ON
		SELECT @ID_DEFAULT, 'NUMERIC TECHNOLOGY', 'NUMERIC TECHNOLOGY' 
		WHERE NOT EXISTS (SELECT 1 FROM FRW_COMPANY WHERE ID_COMPANY = @ID_DEFAULT);
		SET IDENTITY_INSERT FRW_COMPANY OFF
		
		/* SELECIONA A EMPRESA ADMINISTRADORA */
		SET @ID_COMPANY = (SELECT ID_COMPANY FROM FRW_COMPANY WHERE ID_COMPANY = @ID_DEFAULT);

		SET IDENTITY_INSERT FRW_USER ON
		SELECT @ID_DEFAULT, 'admin', @ID_COMPANY, 'Administrador', '6D0bAFpxXvP/sr347/DbjktQRnJhbWV3b3Jr', 0, 1
		WHERE NOT EXISTS (SELECT 1 FROM FRW_USER WHERE ID_USER = @ID_DEFAULT AND USER_LOGIN = 'admin' AND ID_COMPANY = @ID_COMPANY);
		SET IDENTITY_INSERT FRW_USER OFF
		
		/* SELECIONA O USUÁRIO ADMINISTRADOR */
		SET @ID_USER = (SELECT ID_USER FROM FRW_USER WHERE ID_COMPANY = @ID_COMPANY AND USER_LOGIN = 'admin');
		
        /* RELACIONA USUÁRIO COM A EMPRESA */
		SELECT @ID_COMPANY, @ID_USER, 1 
		WHERE NOT EXISTS (SELECT 1 FROM FRW_USER_COMPANY WHERE ID_COMPANY = @ID_COMPANY AND ID_USER = @ID_USER);

        SET IDENTITY_INSERT FRW_PROFILE ON
		SELECT @ID_DEFAULT, @ID_COMPANY, 'ADMINISTRATOR' 
		WHERE NOT EXISTS (SELECT 1 FROM FRW_PROFILE WHERE ID_PROFILE = @ID_DEFAULT AND ID_COMPANY = @ID_COMPANY);
		SET IDENTITY_INSERT FRW_PROFILE OFF
        
        /* SELECIONA PERFIL ADMINISTRADOR */
		SET @ID_PROFILE = (SELECT ID_PROFILE FROM FRW_PROFILE WHERE ID_PROFILE = @ID_DEFAULT);

        /* RELACIONA USUÁRIO COM O PERFIL */
		SELECT @ID_COMPANY, @ID_PROFILE, @ID_USER 
		WHERE NOT EXISTS (SELECT 1 FROM FRW_USER_PROFILE WHERE ID_COMPANY = @ID_COMPANY AND ID_USER = @ID_USER AND ID_PROFILE = @ID_PROFILE);
        
        /* TELAS DO FRAMEWORK */
		SET IDENTITY_INSERT FRW_WINDOW ON


        
		SET IDENTITY_INSERT FRW_WINDOW OFF
		
