        SET IDENTITY_INSERT FRW_PROFILE_WINDOW ON

        SET IDENTITY_INSERT FRW_PROFILE_WINDOW OFF
     
        
		/* MENU DO FRAMEWORK */
        SET IDENTITY_INSERT FRW_MENU ON
        SET IDENTITY_INSERT FRW_MENU OFF
		
