		SET IDENTITY_INSERT FRW_DATATYPE OFF
		
		
		
		SET IDENTITY_INSERT FRW_PARAM_VALUE ON
		SET IDENTITY_INSERT FRW_PARAM_VALUE OFF

	END

COMMIT TRAN